import * as React from "react"
import { pascal } from "naming-style"
import { addPropertyControls, ControlType } from "framer"
// tslint:disable-next-line: ban-ts-ignore
// @ts-ignore
import * as Icons from "@material-ui/icons"

interface Props {
    color?: string
    icon?: string
    theme?: "Filled" | "Outlined" | "Rounded" | "TwoTone" | "Sharp"
    width?: number
    height?: number
}

const defaultProps: Props = {
    color: undefined,
    icon: "",
    theme: "Sharp",
    width: 22,
    height: 22,
}

export const Icon = (props: Props) => {
    const { height, icon: iconProp, theme, width, color, ...other } = props
    const iconName = `${iconProp && pascal(iconProp)}${
        theme === "Filled" ? "" : theme
    }`
    // tslint:disable-next-line: ban-ts-ignore
    // @ts-ignore
    const Icon = Object.keys(Icons).includes(iconName)
        ? Icons[iconName]
        : undefined

    return Icon ? (
        <Icon style={{ width, height, color: `${props.color}` }} {...other} />
    ) : null
}

Icon.defaultProps = defaultProps

addPropertyControls(Icon, {
    color: {
        type: ControlType.Color,
        title: "Color",
    },
    icon: {
        type: ControlType.String,
        title: "Icon",
    },
    theme: {
        type: ControlType.Enum,
        title: "Theme",
        options: ["Filled", "Outlined", "Rounded", "TwoTone", "Sharp"],
    },
})
