import * as React from 'react';
import { PropertyControls, ControlType } from 'framer';
import { Subtitle as _Subtitle } from 'bloomer';

type HeadingSizes = 1 | 2 | 3 | 4 | 5 | 6;

// Define type of property
interface Props {
  text: string;
  isSize: HeadingSizes;
  isSpaced: boolean;
}

export class Subtitle extends React.Component<Props> {
  // Set default properties
  static defaultProps = { text: 'Subtitle' };

  // Items shown in property panel
  static propertyControls: PropertyControls = {
    text: { type: ControlType.String, title: 'Text' },
    isSize: {
      title: 'Size',
      type: ControlType.Number,
      min: 1,
      max: 6,
      step: 1
    },

    isSpaced: { title: 'Spaced', type: ControlType.Boolean }
  };

  render() {
    return <_Subtitle {...this.props}>{this.props.text}</_Subtitle>;
  }
}
