import * as React from 'react';
import { PropertyControls, ControlType } from 'framer';
import { Tag as _Tag } from 'bloomer';

const colors = {
  primary: 'primary',
  info: 'info',
  danger: 'danger',
  warning: 'warning',
  success: 'success',
  white: 'white',
  black: 'black',
  light: 'light',
  dark: 'dark',
  link: 'link'
};

const sizes = {
  large: 'large',
  medium: 'medium',
  small: 'small'
};

// Define type of property
interface Props {
  text: string;
  isColor:
    | 'white'
    | 'light'
    | 'dark'
    | 'black'
    | 'primary'
    | 'info'
    | 'success'
    | 'warning'
    | 'danger';
  isSize: 'large' | 'medium' | 'small';
}

export class Tag extends React.Component<Props> {
  // Set default properties
  static defaultProps = {
    text: 'Tag me'
  };

  // Items shown in property panel
  static propertyControls: PropertyControls = {
    text: { type: ControlType.String, title: 'Text' },
    isColor: {
      title: 'Color',
      type: ControlType.Enum,
      optionTitles: Object.keys(colors),
      options: Object.values(colors)
    },
    isSize: {
      title: 'Size',
      type: ControlType.SegmentedEnum,
      optionTitles: Object.keys(sizes),
      options: Object.values(sizes)
    }
  };

  render() {
    return <_Tag {...this.props}>{this.props.text}</_Tag>;
  }
}
